import React from 'react';
import ToDoListItem from './ToDoListItem';



export default class ToDoList extends React.Component{

    toDoList = ["Go to School", "Do Homeworks", "Come home"];

    deleteItem(index){
        var newArray = [...this.toDoList]
        newArray.splice(index, 1)
        this.toDoList = newArray
    }

    render(){     
        var listItems = this.toDoList.map((listItem, index)=>{
            console.log(index)
            return <ToDoListItem item={listItem} key={index} id={index} deleteItem={this.deleteItem}></ToDoListItem>
        })
        return listItems;
    }

}
