import React from 'react';

export default class ToDoListItem extends React.Component{

    render(){

        const tasks = this.props.tasks.map((listItem, index)=>{
            return <li class="list-group-item" key={index} id={index}>{listItem}</li>
        });

        return tasks;
    }

}
